package mafsa

import (
	"bytes"
	"testing"
)

func TestEncoder(t *testing.T) {
	tree := New()
	tree.Insert("dog")
	tree.Insert("dogs")
	tree.Insert("hello")
	tree.Insert("jello")
	tree.Finish()

	enc := new(Encoder)
	actual, err := enc.Encode(tree)
	if err != nil {
		t.Errorf("Encode produced an error: %v", err)
	}

	if !bytes.Equal(actual, encodedTestTree) {
		t.Errorf("\nExpected:\n%v\nGot:\n%v", encodedTestTree, actual)
	}
}

var encodedTestTree = []byte{
	0x01, 0x06, 0x01, 0x04, 0x00, 0x00,
	0x64, 0x00, 0x00, 0x00, 0x00, 0x04,
	0x68, 0x00, 0x00, 0x00, 0x00, 0x05,
	0x6a, 0x02, 0x00, 0x00, 0x00, 0x06,
	0x6f, 0x02, 0x00, 0x00, 0x00, 0x07,
	0x65, 0x02, 0x00, 0x00, 0x00, 0x08,
	0x65, 0x02, 0x00, 0x00, 0x00, 0x08,
	0x67, 0x03, 0x00, 0x00, 0x00, 0x09,
	0x6c, 0x02, 0x00, 0x00, 0x00, 0x0a,
	0x73, 0x03, 0x00, 0x00, 0x00, 0x00,
	0x6c, 0x02, 0x00, 0x00, 0x00, 0x0b,
	0x6f, 0x03, 0x00, 0x00, 0x00, 0x00,
}
